const baseAPI = '/';

const flightService = {
  get() {
    return new Promise((resolve, reject) => {
      fetch(`${baseAPI}/flights`)
        .then(response => response.json())
        .then(json => resolve(json))
        .catch(err => {
          reject(err);
        });
    });
  },

  getUserFlights(user) {
    return new Promise((resolve, reject) => {
      fetch(`${baseAPI}/flights`)
        .then(response => response.json())
        .then(json => resolve(json))
        .catch(err => {
          reject(err);
        });
    });
  }
};

export default flightService;
